    private void btnAddActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAddActionPerformed
           java.awt.EventQueue.invokeLater(new Runnable() {
            public void run() {
                
                AddVencimiento dialog = new AddVencimiento(new javax.swing.JFrame(), true,0);
                dialog.setLocationRelativeTo(null); // Center on the screen
                dialog.setVisible(true);
                updateVencimientosJTable();
            }
        });
    }//GEN-LAST:event_btnAddActionPerformed

    private void modificarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_modificarActionPerformed
try
{
        final int selectedRow = jTable1.getSelectedRow();//.getLeadSelectionIndex();
        /* Create and display the dialog */
        java.awt.EventQueue.invokeLater(new Runnable() {
            public void run() {
                AddVencimiento dialog = new AddVencimiento(new javax.swing.JFrame(), true, Integer.parseInt(jTable1.getValueAt(selectedRow, 0).toString()));
                dialog.setLocationRelativeTo(null);
                dialog.setVisible(true);
            }
        });
       updateVencimientosJTable();
}
catch(ArrayIndexOutOfBoundsException e){
            JOptionPane.showConfirmDialog(getParent(),"Primero debe seleccionar una fila.");
        }
    }//GEN-LAST:event_modificarActionPerformed

    private void jTable1MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jTable1MouseClicked
        modificar.setEnabled(true);
        eliminar.setEnabled(true);
    }//GEN-LAST:event_jTable1MouseClicked
