    private void jTable1FocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_jTable1FocusLost
        modificar.setEnabled(false);
        eliminar.setEnabled(false);
    }//GEN-LAST:event_jTable1FocusLost

    private void eliminarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_eliminarActionPerformed
       int res=JOptionPane.showConfirmDialog(this.getParent(), "¿Seguro que desea eliminar este vencimiento?");
       if (res==0)
       {
       int selectedRow = jTable1.getSelectedRow();
       BizEnt.db.deleteVencimiento(Integer.parseInt(jTable1.getValueAt(selectedRow, 0).toString()));
       updateVencimientosJTable();
       }
    }//GEN-LAST:event_eliminarActionPerformed
    public static void updateVencimientosJTable() {
        jTable1.getColumnModel().getColumn(0).setMinWidth(0);
        jTable1.getColumnModel().getColumn(0).setMaxWidth(0);
        jTable1.getColumn("Fecha").setCellRenderer(new DateRenderer("dd-MM-yyyy"));
        jTable1.getColumn("Monto").setCellRenderer(new DecimalRenderer("###,##0.00"));
    }
    public static void setLabel(String t)
    {
        jLabel1.setText(t);
    }
